import { Input, Form, Radio, QRCode, Modal, Button } from "antd";
import "./ThuePhong.css";
import { useEffect, useState } from "react";
import { getHostel } from "../../services/hostelServices";
import { taoNguoiThue } from "../../services/thuephongServices";
const { TextArea } = Input;

export default function ThuePhong(props) {
  const [sale, setSale] = useState(0);
  const [price, setPrice] = useState(0);
  const [time, setTime] = useState(0);
  const [finalPrice, setFinalPrice] = useState(0);
  const [soPhong, setSoPhong] = useState(0);
  const [inforRenter, setInforRenter] = useState({});
  const [isModalOpen, setIsModalOpen] = useState(false);

  const { id } = props;
  console.log(id);

  const handleChange = (e) => {
    switch (e.target.value) {
      case 50:
        setSale(5);
        break;
      case 70:
        setSale(7);
        break;
      case 100:
        setSale(10);
        break;
      default:
        break;
    }
  };

  useEffect(() => {
    const fetchApi = async () => {
      const result = await getHostel(id);
      if (result) {
        setPrice(result.price);
        console.log(result);
      }
    };
    fetchApi();
  }, [id]);

  const handleChange1 = (e) => {
    setTime(e.target.value);
    console.log(time);
  };

  const handleChange2 = (e) => {
    setSoPhong(e.target.value);
  };

  const uoctinh = () => {
    let x =
      soPhong *
      (price * 1000000 * time - (price * 1000000 * time * sale) / 100);
    console.log(soPhong);
    console.log(price);
    console.log(time);
    console.log(sale);
    setFinalPrice(x);
    console.log(finalPrice);
  };

  const handleOk = () => {
    setIsModalOpen(false);
  };

  const handleFinish = (value) => {
    setInforRenter({
      id,
      value,
    });
    const fetchApi = async () => {
      const result = await taoNguoiThue(inforRenter);
      if (result) {
        setIsModalOpen(true);
      }
    };
    fetchApi();
  };

  return (
    <>
      <h1 className="thuephong-title">Thuê phòng trực tuyến với Stayhub</h1>
      <h2 className="thuephong-title2">Ưu đãi lên đến 10%</h2>
      <Form
        name="trigger"
        style={{
          maxWidth: "100%",
        }}
        layout="vertical"
        autoComplete="off"
        onFinish={handleFinish}
      >
        <Form.Item
          hasFeedback
          label="Họ tên"
          name="nameInput"
          style={{ width: "100%"}}
        >
          <Input required />
        </Form.Item>

        <Form.Item
          hasFeedback
          label="Số điện thoại"
          name="phoneInput"
          style={{ width: "100%"}}
        >
          <Input required />
        </Form.Item>

        <Form.Item
          hasFeedback
          label="Gmail"
          name="gmailInput"
          style={{ width: "100%" }}
        >
          <Input required />
        </Form.Item>

        <Form.Item>
          <label className="lableRadio">Số phòng thuê</label>
          <Radio.Group
            name="radiogroup"
            style={{ marginLeft: "20px" }}
            onChange={handleChange2}
          >
            <Radio value={1}>1 phòng</Radio>
            <Radio value={2}>2 phòng</Radio>
            <Radio value={3}>3 phòng</Radio>
          </Radio.Group>
        </Form.Item>

        <Form.Item>
          <label className="lableRadio">Kỳ hạn thuê</label>
          <Radio.Group
            name="radiogroup"
            style={{ marginLeft: "20px" }}
            onChange={handleChange1}
          >
            <Radio value={1}>1 tháng</Radio>
            <Radio value={3}>3 tháng</Radio>
            <Radio value={6}>6 tháng</Radio>
            <Radio value={12}>12 tháng</Radio>
          </Radio.Group>
        </Form.Item>

        <Form.Item>
          <label className="lableRadio">Đặt cọc trước</label>
          <Radio.Group
            name="radiogroup"
            style={{ marginLeft: "20px" }}
            onChange={handleChange}
          >
            <Radio value={50}>50%</Radio>
            <Radio value={70}>70%</Radio>
            <Radio value={100}>100%</Radio>
          </Radio.Group>
          <span> được giảm {sale}%</span>
        </Form.Item>

        <Form.Item
          hasFeedback
          label="Lời nhắn với chủ trọ"
          name="messageInput"
          style={{ width: "100%" }}
        >
          <TextArea rows={4} />
        </Form.Item>

        <Button type="primary" htmlType="button" onClick={uoctinh}>
          Ước tính
        </Button>
        <h3>Tổng chi phí: {finalPrice} đồng.</h3>

        <Form.Item style={{ display: "inline-block" }}>
          <label className="lableRadio">Chọn phương thức thanh toán</label>{" "}
          <br />
          <Radio.Group
            name="radiogroup"
            defaultValue={1}
            style={{ marginLeft: "20px" }}
            onChange={handleChange}
          >
            <Radio value="vnpay">
              <img
                src="data:image/png;base64,"
                alt=""
              />
            </Radio>
          </Radio.Group>
        </Form.Item>

        <Form.Item className="submitArea">
          <input type="submit" className="thuephongSubmit" value="Xác nhận" />
        </Form.Item>

        <Modal open={isModalOpen} onOk={handleOk}>
          <label className="lableRadio">Vui lòng quét mã để thanh toán</label>{" "}
          <br />
          <QRCode
            errorLevel="H"
            value={finalPrice}
            icon="https://downloadlogomienphi.com/sites/default/files/logos/download-logo-vector-vnpay-2021-doc-mien-phi.jpg"
          />
        </Modal>
      </Form>
    </>
  );
}
